    profilePage: {
        posts: [
            {id: 1, message: 'Hi, how are you?', likesCount: 0},
            {id: 2, message: 'It\'s my first post', likesCount: 23}
        ],
        newPostText: 'write something'
    },

    dialogsPage: {
        dialogs: [
            {id: 1, name: 'Cyril'},
