const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
    users: [
        {
            id: 1,
            fullName: 'Tim',
            followed: false,
            status: 'Hamburgers and hot-dogs too, I wanna have a BBQ',
            location: {city: 'Los-Angeles', country: 'USA'}
        },
        {
            id: 2,
            fullName: 'Eric',
            followed: true,
            status: 'I like parties, I like fun, I wanna live in a hamburger bun',
            location: {city: 'Los-Angeles', country: 'USA'}
        },
        {
            id: 3,
            fullName: 'DJ Douggpound',
            followed: true,
            status: 'Are you ready for the remix?',
            location: {city: 'Los-Angeles', country: 'USA'}
        },
        {
            id: 4,
            fullName: 'Dr.Steve Brule',
            followed: false,
            status: 'For your health!',
            location: {city: 'Los-Angeles', country: 'USA'}
        },
        {
            id: 5,
            fullName: 'Andre',
            followed: false,
            status: 'Do you believe in da lies of society?!',
            location: {city: 'Los-Angeles', country: 'USA'}
        }
    ],
};

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: true}
                    }
                    return u;
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        case SET_USERS: {
            return {
                ...state,
                users: [...state.users, ...action.users]
            }
        }
        default:
            return state;
    }
}

export const followAC = (userId) => ({type: FOLLOW, userId})

export const unfollowAC = (userId) => ({type: UNFOLLOW, userId})

export const setUsersAC = (users) => ({type: SET_USERS, users})

export default usersReducer;
